function time_travle () {
    if (sprites.readDataString(mySprite, "time") == "present") {
        sprites.setDataString(mySprite, "time", " future")
        for (let index = 0; index < 5; index++) {
            for (let index = 0; index <= 15; index++) {
                color.setColor(index, color.rgb(randint(0, 300), randint(0, 300), randint(0, 300)))
            }
            pause(100)
        }
        color.setPalette(
        color.originalPalette
        )
        if (level == 1) {
            tiles.setSmallTilemap(tilemap`level8`)
        }
        if (level == 2) {
            scene.setBackgroundColor(9)
            tiles.setSmallTilemap(tilemap`level14`)
        }
        if (level == 3) {
            scene.setBackgroundColor(9)
            tiles.setSmallTilemap(tilemap`level24`)
        }
    } else if (sprites.readDataString(mySprite, "time") == " future") {
        sprites.setDataString(mySprite, "time", " past")
        for (let index = 0; index < 5; index++) {
            for (let index = 0; index <= 15; index++) {
                color.setColor(index, color.rgb(randint(0, 300), randint(0, 300), randint(0, 300)))
            }
            pause(100)
        }
        color.setPalette(
        color.originalPalette
        )
        if (level == 1) {
            tiles.setSmallTilemap(tilemap`level6`)
        }
        if (level == 2) {
            scene.setBackgroundColor(1)
            tiles.setSmallTilemap(tilemap`level18`)
        }
        if (level == 3) {
            scene.setBackgroundColor(1)
            tiles.placeOnRandomTile(mySprite, assets.tile`myTile12`)
            tiles.setSmallTilemap(tilemap`level26`)
        }
    } else if (sprites.readDataString(mySprite, "time") == " past") {
        sprites.setDataString(mySprite, "time", "present")
        for (let index = 0; index < 5; index++) {
            for (let index = 0; index <= 15; index++) {
                color.setColor(index, color.rgb(randint(0, 300), randint(0, 300), randint(0, 300)))
            }
            pause(100)
        }
        color.setPalette(
        color.originalPalette
        )
        if (level == 1) {
            tiles.setSmallTilemap(tilemap`level5`)
        }
        if (level == 2) {
            scene.setBackgroundColor(9)
            tiles.setSmallTilemap(tilemap`level10`)
        }
        if (level == 3) {
            scene.setBackgroundColor(9)
            tiles.setSmallTilemap(tilemap`level20`)
        }
    }
    textSprite2.setText(sprites.readDataString(mySprite, "time"))
}
scene.onOverlapTile(SpriteKind.the_vilon, assets.tile`myTile5`, function (sprite, location) {
    flying = false
    mySprite.destroy()
    if (level == 4) {
        the_bad_guy.destroy()
    }
    game.splash("The evil villain beet you.")
    textSprite2.setFlag(SpriteFlag.Invisible, true)
    tiles.setSmallTilemap(tilemap`level12`)
    go()
    pause(500)
})
scene.onOverlapTile(SpriteKind.Player, assets.tile`myTile5`, function (sprite, location) {
    flying = false
    if (level == 1) {
        game.showLongText("You have finished testing the Time Wings, now you have to help us.", DialogLayout.Bottom)
        game.splash("chapter 1 complete", "chapter 2 unlocked")
        blockSettings.writeString("chapter_unlocked", "Chapter 1")
        blockSettings.writeString("chapter_unlocked1", "Chapter 2")
        mySprite.destroy()
        Chapter()
    }
    if (level == 2) {
        game.showLongText("You have helped the other testers get back.", DialogLayout.Bottom)
        game.showLongText("But there are still some testers out there we need to rescue.", DialogLayout.Bottom)
        game.splash("chapter 2 complete", "chapter 3 unlocked")
        blockSettings.writeString("chapter_unlocked2", "Chapter 3")
        mySprite.destroy()
        Chapter()
    }
    if (level == 3) {
        game.showLongText("You have helped the other testers get back.", DialogLayout.Bottom)
        game.showLongText("There is still one more tester out there and he had the 2.0 Time Wings.", DialogLayout.Bottom)
        game.splash("chapter 3 complete", "chapter 4 unlocked")
        blockSettings.writeString("chapter_unlocked3", "Chapter 4")
        mySprite.destroy()
        Chapter()
    }
    if (level == 4) {
        tiles.setSmallTilemap(tilemap`level36`)
        mySprite.destroy()
        endof4story = true
    }
})
