forever(function () {
    if (flying && (scene.cameraProperty(CameraProperty.Left) > mySprite.x && level == 4)) {
        flying = false
        mySprite.destroy()
        if (level == 4) {
            the_bad_guy.destroy()
        }
        game.splash("You hit a wall to hard!")
        textSprite2.setFlag(SpriteFlag.Invisible, true)
        tiles.setSmallTilemap(tilemap`level12`)
        go()
        pause(500)
    } else {
        if (flying && ((mySprite.isHittingTile(CollisionDirection.Left) || mySprite.isHittingTile(CollisionDirection.Right)) && level >= 2)) {
            flying = false
            mySprite.destroy()
            if (level == 4) {
                the_bad_guy.destroy()
            }
            game.splash("You hit a wall to hard!")
            textSprite2.setFlag(SpriteFlag.Invisible, true)
            tiles.setSmallTilemap(tilemap`level12`)
            go()
            pause(500)
        }
    }
})
