forever(function () {
    timer.background(function () {
        if (flying == true) {
            if (!(controller.down.isPressed()) && !(controller.up.isPressed())) {
                mySprite.ay = 10
            }
            if (10 <= speed) {
                mySprite.vx = speed
            } else {
                mySprite.vx = 10
                speed = 10
            }
            pause(100)
        }
    })
    if (flying == true) {
        if (controller.up.isPressed()) {
            if (!(sprites.readDataNumber(mySprite, "2speedy") >= 0)) {
                mySprite.vy = sprites.readDataNumber(mySprite, "2speedy")
            } else {
                sprites.setDataNumber(mySprite, "2speedy", -10)
            }
            sprites.changeDataNumberBy(mySprite, "2speedy", 10)
            mySprite.setImage(img`
                b b b b . . . . . 
                . b b b b . . 4 4 
                . 6 2 2 4 4 4 4 4 
                6 6 2 2 d d f d f 
                . 6 2 2 d d f d f 
                6 6 2 2 d d d d d 
                . 6 2 2 . . . . . 
                . . . . . . . . . 
                . . . . . . . . . 
                `)
            transformSprites.rotateSprite(mySprite, 50)
            pause(200)
            speed += -10
        } else if (controller.down.isPressed()) {
            mySprite.setImage(img`
                b b b b . . . . . 
                . b b b b . . 4 4 
                . 6 2 2 4 4 4 4 4 
                6 6 2 2 d d f d f 
                . 6 2 2 d d f d f 
                6 6 2 2 d d d d d 
                . 6 2 2 . . . . . 
                . . . . . . . . . 
                . . . . . . . . . 
                `)
            transformSprites.rotateSprite(mySprite, 100)
            mySprite.vy = sprites.readDataNumber(mySprite, "2speedx")
            sprites.changeDataNumberBy(mySprite, "2speedx", 10)
            if (!(sprites.readDataNumber(mySprite, "2speedx") >= 101)) {
                sprites.setDataNumber(mySprite, "2speedx", 50)
            }
            speed += 10
            pause(200)
        }
    }
})
